import Layout from '../../components/layout';
import Fetch from 'isomorphic-unfetch';

const Post = props => (
  <Layout>
    <h1>{props.name}</h1>
    <p>Colour: {props.colour}</p>
  </Layout>
);

Post.getInitialProps = async function () {

  const res = await fetch(`https://localhost:4040/fruits/name/:name/colour/:colour`);
  const fruits = await res.json();

  console.log(`Fetched fruits: ${name}`);

  return { fruits };
};

export default Post;
