import Header from './Header';
import Footer from './Footer';


const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #DDD'
};

const Layout = properties => (
  <div>
  <div style={layoutStyle}>
    <Header />
    {properties.children}
    </div>
    <Footer />
  </div>

);

export default Layout;
